 import {SyntheticEvent, useState} from "react";
import {Navigate} from 'react-router-dom';
const Register = () => {
    const [name,setName]=useState("");
    const [email,setEmail]=useState("");
    const [password,setPassword]=useState("");
    const [redirect,setRedirect]=useState(false);


    const submit=async(e:SyntheticEvent)=>{
        e.preventDefault();
        await fetch('http://localhost:8000/api/register',{
            method:'POST',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                name,
                email,
                password
            })
        });
        setRedirect(true);
        
    }
    if(redirect){
        return <Navigate to='/login' />
    }

  return (
    // <div className="form-signin w-100 m-auto"><form onSubmit={submit}>
    // <h1 className="h3 mb-3 fw-normal">Please Register</h1>

    // <div className="form-floating">
    //     <input type="Name" className="form-control"  onChange={e=>setName(e.target.value)} required placeholder="name"  />
        
    // </div>
    // <div className="form-floating">
    //     <input type="email" className="form-control" onChange={e=>setEmail(e.target.value)} required  placeholder="name@example.com"  />
        
    // </div>
    // <div className="form-floating">
    //     <input type="password" className="form-control" required onChange={e=>setPassword(e.target.value)} placeholder="Password"  />
        
    // </div>

     
    // <button className="btn btn-primary w-100 py-2" type="submit">Submit</button>
    
    // </form></div>

<div className="container-xxl my-md-4 bd-layout">

    
<div className="form-signin w-100 m-auto">
    <form onSubmit={submit}>
        <h1 className="h3 mb-3 fw-normal">Please Register</h1>

        <div className="mb-3">
          <label htmlFor="exampleFormControlInput1" className="form-label">Name</label>
          <input type="text" className="form-control"  id="exampleFormControlInput1" placeholder="name" required  onChange={(e)=>{setName(e.target.value)}} />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
          <input type="email" className="form-control"  id="exampleFormControlInput1" placeholder="name@example.com" required  onChange={(e)=>{setEmail(e.target.value)}} />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleFormControlInput2" className="form-label">Password</label>
          <input type="password"  id="exampleFormControlInput2" className="form-control" onChange={(e)=>setPassword(e.target.value)} required placeholder="Password"  />
        </div>
        <button className="btn btn-primary w-100 py-2" type="submit">Sign in</button>
        
    </form>
</div>
</div>
  )
}

export default Register
