import {Navigate} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
import { useTable, TableOptions, Column } from "react-table";
import React  from "react";
import {SyntheticEvent,useState,useEffect} from "react";


type Cols = { taskuuid: string; title: string,description:string,status:string };

const TaskList= (props:{name:string}) =>{

  // const data = React.useMemo(
  //   (): Cols[] => [
  //     {
  //       taskuuid: "Hello",
  //       title: "World",
  //       description:"desc",
  //       status:"pending"
  //     },
  //     {
  //       taskuuid: "react-table",
  //       title: "rocks",
  //       description:"desc",
  //       status:"pending"
  //     },
  //     {
  //       taskuuid: "whatever",
  //       title: "you want",
  //       description:"desc",
  //       status:"pending"
  //     }
  //   ],
  //   []
  // );
 

  const [data, setData] = useState([]);
 
  const getData = () => {
      fetch("http://localhost:8000/api/tasks/tasklist",{
        method:'GET',
        headers:{'Content-Type':'application/json'},
        credentials:'include',
         
      })
          .then((response) => response.json())
          .then((data) => {
             
              setData(data);
              
          });
  }

  useEffect(() => {
      getData();
  }, []);


  const columns: Column<{ taskuuid: string; title: string,description:string,status:string }>[] = React.useMemo(
    () => [
      {
        Header: "Uuid",
        accessor: "taskuuid", // accessor is the "key" in the data
        show: false
      },
      {
        Header: "title",
        accessor: "title"
      }
      ,
      {
        Header: "description",
        accessor: "description"
      }
      ,
      {
        Header: "status",
        accessor: "status"
      }
    ],
    []
  );

  const initialState = { hiddenColumns: ['taskuuid'] };
  const options: TableOptions<{ taskuuid: string; title: string,description:string,status:string }> = {
    data,
    columns,
    initialState
  };
  const [redirect,setRedirect]=useState(false);
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow
  } = useTable(options);

  const btnadd=async(e:SyntheticEvent)=>{
    e.preventDefault();
     
    setRedirect(true);
  }

  if(redirect){
    
    return <Navigate to='/task' />
  }

  return (
    <div className="container-xxl my-md-4 bd-layout">

  
    <div>{props.name?<h1>Task List page :{props.name}</h1>:'Not validat user'}</div>
    <div className="mb-3"><button className="btn btn-primary" onClick={btnadd}>Add New</button></div>
    <table className="table table-striped table-bordered "  {...getTableProps()} >
      <thead> 
        {headerGroups.map((headerGroup) => (
          <tr className="row"  {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th className=" col-2 "
                {...column.getHeaderProps()}
                
              >
                {column.render("Header")}
              </th>
            ))}
            <th className=" col-4 ">Edit/Delete</th>
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row) => {
          prepareRow(row);
          return (
            <tr className="row" {...row.getRowProps()}>
              {row.cells.map((cell) => {
                return (
                  <td className="col-2"
                    {...cell.getCellProps()}
                     
                  >
                    {cell.render("Cell")}
                  </td>
                );
              })}
              <td className=" col-4"><a href="#">Edit</a> <a href="#">Delete</a></td>
            </tr>
          );
        })}
      </tbody>
    </table>

    </div>
  );
}

export default   TaskList;
